import React, { useEffect, useState } from "react";
import "./App.css";
import autumnList from "./data/Høst.json";

const App = () => {
  const words = autumnList.ord;

  // States
  const [userName, setUserName] = useState("");
  const [isGameStarted, setIsGameStarted] = useState(false);
  const [inputValue, setInputValue] = useState("");
  const [randomWord, setRandomWord] = useState("");
  const [score, setScore] = useState(0);
  const [remainingTime, setRemainingTime] = useState(120);
  const [highscores, setHighscores] = useState([]);

  useEffect(() => {
    const savedHighscores = localStorage.getItem('highscores');
    if (savedHighscores) {
      setHighscores(JSON.parse(savedHighscores));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('highscores', JSON.stringify(highscores));
  }, [highscores]);

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleUserNameChange = (event) => {
    setUserName(event.target.value);
  };

  const handleStartGame = () => {
    if (!userName.trim()) {
      alert("Vennligst skriv inn navnet ditt for å starte spillet!");
      return;
    }
    setIsGameStarted(true);
    setRandomWord(getRandomWord());
  };

  const handleKeyUp = (event) => {
    if (event.key === " " && remainingTime > 0) {
      if (inputValue.trim() === randomWord) {
        setScore(score + 50);
      } else {
        setScore(score - 25);
      }
      setInputValue("");
      setRandomWord(getRandomWord());
    }
  };

  const handleNameInputKeyUp = (event) => {
    if (event.key === "Enter") {
      handleStartGame();
    }
  };

  useEffect(() => {
    if (isGameStarted && remainingTime > 0) {
      const timer = setInterval(() => {
        setRemainingTime(prevTime => prevTime - 1);
      }, 1000);

      return () => clearInterval(timer);
    }
  }, [isGameStarted, remainingTime]);

  useEffect(() => {
    if (remainingTime <= 0) {
      setHighscores(prevScores => [...prevScores, { name: userName, score: score }].sort((a, b) => b.score - a.score).slice(0, 10));
      setIsGameStarted(false);
    }
  }, [remainingTime]);

  const getRandomWord = () => {
    return words[Math.floor(Math.random() * words.length)];
  };

  if (!isGameStarted && remainingTime !== 0) {
    return (
      <div className="App">
        <input
          type="text"
          value={userName}
          onChange={handleUserNameChange}
          onKeyUp={handleNameInputKeyUp}
          placeholder="Skriv inn navnet ditt"
        />
        <button onClick={handleStartGame}>Start spill</button>
      </div>
    );
  }

  if (remainingTime <= 0) {
    return (
      <div className="App">
        <h1>Highscores</h1>
        <ol>
          {highscores.map((entry, index) => (
            <li key={index}>
              {entry.name}: {entry.score}
            </li>
          ))}
        </ol>
        <button onClick={() => setRemainingTime(120)}>Play Again</button>
      </div>
    );
  }

  return (
    <div className="App">
      <h1>{randomWord}</h1>
      <input
        type="text"
        value={inputValue}
        onChange={handleInputChange}
        onKeyUp={handleKeyUp}
      />
      <p>Score: {score}</p>
      <p>Tid igjen: {remainingTime}</p>
    </div>
  );
};

export default App;
