import "./App.css";
import data from "./Høst.json";
import { useEffect, useState } from "react";

function App() {
  /*   console.log(data.ord); */
  const [round, setRound] = useState(1);
  const [score, setScore] = useState(0);
  const [correctKeyPresses, setCorrectKeyPresses] = useState(0);
  const [wrongKeyPresses, setWrongKeyPresses] = useState(0);

  const setRoundFunction = () => {
    if (round === data.ord.length) {
      setRound(1);
    } else {
      setRound((prevState) => prevState + 1);
    }
  };

  const handleCorrectKeyPress = () => {
    const pointsPerCorrectKeyPress = 1;
    setScore(score + pointsPerCorrectKeyPress);
    setCorrectKeyPresses(correctKeyPresses + 1);
  };

  const handleWrongKeyPress = () => {
    const pointsPerWrongKeyPress = -1;
    const maxPointsToDeductPerWord = -5;
    const pointsToDeduct = Math.min(
      pointsPerWrongKeyPress * (wrongKeyPresses + 1),
      maxPointsToDeductPerWord
    );

    setScore(score + pointsToDeduct);
    setWrongKeyPresses(wrongKeyPresses + 1);
  };

  const handleWordCompletion = () => {
    const wordCompletionBonus = 50;
    setScore(score + wordCompletionBonus);
  };

  return (
    <div className="App">
      <p>{data.ord[round - 1]}</p>
      <button onClick={setRoundFunction}>Bytt ord</button>
    </div>
  );
}

export default App;
