import "./App.css";
import autumnList from "./data/Høst.json";
import { useEffect, useState } from "react";

const App = () => {
  const words = autumnList.ord;
  const getRandomWord = (wordList) => {
    const index = Math.floor(Math.random() * wordList.length);
    return wordList[index];
  };
  const [inputValue, setInputValue] = useState("");
  const [randomWord, setRandomWord] = useState(getRandomWord(words));
  const [indexWord, setIndexWord] = useState()
  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  useEffect(() => {
    console.log(inputValue.split("")[0], randomWord.split("")[0]);
  }, [inputValue]);



  return (
    <div>
      <h1>Random Word: {randomWord}</h1>
      <input
        type="text"
        value={inputValue}
        onChange={handleInputChange}
      />
      <button onClick={() => setRandomWord(getRandomWord(words))}>
        Get Another Random Word{" "}
      </button>
    </div>
  );
};

export default App;
