import React, { useEffect, useState } from "react";
import "./App.css";
import autumnList from "./data/Høst.json";

const App = () => {
  const words = autumnList.ord;

  function getRandomWord(wordList) {
    const index = Math.floor(Math.random() * wordList.length);
    return wordList[index];
  }

  const [inputValue, setInputValue] = useState("");
  const [randomWord, setRandomWord] = useState("");
  const [indexWord, setIndexWord] = useState(0);
  const [score, setScore] = useState(0);
  const [remainingWords, setRemainingWords] = useState([...words]);
  const [deductions, setDeductions] = useState(0);

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleNewWord = () => {
    if (remainingWords.length > 1) {
      const updatedRemainingWords = remainingWords.filter(
        (word) => word !== randomWord
      );
      setRemainingWords(updatedRemainingWords);
      setRandomWord(getRandomWord(updatedRemainingWords));
      setIndexWord(0);
      setDeductions(0);
    } else {
      alert("No more words remaining!");
    }
  };

  useEffect(() => {
    setScore(0);
  }, []);

  useEffect(() => {
    if (inputValue[indexWord] && randomWord[indexWord]) {
      if (inputValue[indexWord] === randomWord[indexWord]) {
        setScore((prevScore) => prevScore + 1);
      } else {
        if (deductions < 5) {
          setScore((prevScore) => prevScore - 1);
          setDeductions((prevDeductions) => prevDeductions + 1);
        }
      }

      setIndexWord((lastIndex) => lastIndex + 1);

      // Check if the user has correctly typed the entire word
      if (inputValue === randomWord && indexWord === randomWord.length - 1) {
        setScore((prevScore) => prevScore + 50); // Add 50 points for the correct word ending
        handleNewWord();
        setInputValue("");
      } else if (indexWord >= randomWord.length - 1) {
        handleNewWord();
        setInputValue("");
      }

    }
  }, [inputValue, randomWord, indexWord, remainingWords, deductions]);

  useEffect(() => {
    setRandomWord(getRandomWord(remainingWords));
    setIndexWord(0);
    setInputValue("");
    setDeductions(0);
  }, [remainingWords]);

  return (
    <div className="center-content">
      <h1>Skriv ordet: {randomWord}</h1>
      <input type="text" value={inputValue} onChange={handleInputChange} />
      <p>Poengscore: {score}</p>
    </div>
  );
};

export default App;
