import "./App.css";
import autumnList from "./data/Høst.json";
import { useEffect, useState } from "react";

const App = () => {
  const words = autumnList.ord;
  const getRandomWord = (wordList) => {
    const index = Math.floor(Math.random() * wordList.length);
    return wordList[index];
  };
  const [inputValue, setInputValue] = useState("");
  const [randomWord, setRandomWord] = useState(getRandomWord(words));
  const [indexWord, setIndexWord] = useState(0);
  const [score, setScore] = useState(0);

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  useEffect(() => {
    // Check if inputValue and randomWord have characters at indexWord
    if (inputValue[indexWord] && randomWord[indexWord]) {
      if (inputValue[indexWord] === randomWord[indexWord]) {
        // If characters match, increment the score by 1
        setScore((prevScore) => prevScore + 1);
      } else {
        // If characters do not match, decrement the score by 1
        setScore((prevScore) => prevScore - 1);
      }

      // Increment indexWord
      setIndexWord((lastIndex) => lastIndex + 1);
    }
  }, [inputValue, randomWord, indexWord]);



  return (
    <div>
      <h1>Random Word: {randomWord}</h1>
      <input type="text" value={inputValue} onChange={handleInputChange} />
      <button onClick={() => setRandomWord(getRandomWord(words))}>
        Get Another Random Word{" "}
      </button>
    </div>
  );
};

export default App;
