import React, { useEffect, useState } from "react";
import "./App.css";
import autumnList from "./data/Høst.json";

const App = () => {
  const words = autumnList.ord;

  function getRandomWord(wordList) {
    const index = Math.floor(Math.random() * wordList.length);
    return wordList[index];
  }

  const [inputValue, setInputValue] = useState("");
  const [randomWord, setRandomWord] = useState("");
  const [indexWord, setIndexWord] = useState(0);
  const [score, setScore] = useState(0);
  const [remainingWords, setRemainingWords] = useState([...words]);

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleNewWord = () => {
    if (remainingWords.length > 1) {
      const updatedRemainingWords = remainingWords.filter(
        (word) => word !== randomWord
      );
      setRemainingWords(updatedRemainingWords);
      setRandomWord(getRandomWord(updatedRemainingWords));
      setIndexWord(0);
    } else {
      alert("No more words remaining!");
    }
  };

  useEffect(() => {
    // Initialize the score only once when the component mounts
    setScore(0);
  }, []);

  useEffect(() => {
    if (inputValue[indexWord] && randomWord[indexWord]) {
      if (inputValue[indexWord] === randomWord[indexWord]) {
        setScore((prevScore) => prevScore + 1);
      } else {
        setScore((prevScore) => prevScore - 1);
      }

      setIndexWord((lastIndex) => lastIndex + 1);

      if (indexWord >= randomWord.length - 1) {
        // Check if the word is completed before getting a new one
        handleNewWord();
        setInputValue(""); // Clear input when getting a new word
      }
    }
  }, [inputValue, randomWord, indexWord, remainingWords]);

  useEffect(() => {
    // When the component mounts and when remainingWords changes, get a new random word
    setRandomWord(getRandomWord(remainingWords));
    setIndexWord(0);
    setInputValue(""); // Clear input when getting a new word
  }, [remainingWords]);

  return (
    <div>
      <h1>Random Word: {randomWord}</h1>
      <input type="text" value={inputValue} onChange={handleInputChange} />
      <button onClick={handleNewWord}>Get Another Random Word</button>
      <p>Score: {score}</p>
    </div>
  );
};

export default App;
